import {useSelector, useDispatch} from 'react-redux'
import {useEffect} from 'react'
import {loadEmployerSuccess} from '../../redux/actions/employerActions'
import * as employerApi from '../../api/employersApi';

const useApp = (initalValue) => {
    const employers = useSelector(state =>state.employers);
    const dispatch = useDispatch();
    const loadEmployer = () => dispatch(loadEmployers());

    useEffect(() => {
        employerApi.getEmployers().then((employer) => {
            dispatch(loadEmployerSuccess(employer));
        })
        .catch((error) => {
            throw error;
        });
        return () => {

        };
    }, [])

    return{employers};
}

export default useApp;
