import {useSelector, useDispatch} from 'react-redux'
import {useEffect} from 'react'
import {loadEmployerSuccess} from '../../redux/actions/employerActions'
import * as employerApi from '../../api/employersApi'
import {beginApiCall} from '../../redux/actions/apiStatusActions'

const useApp = (initalValue) => {
    const employers = useSelector(state =>state.employers);
    const dispatch = useDispatch();
    const loadEmployers = () => {
        return function (dispatch) {
            dispatch(beginApiCall());
            return employerApi
                .getEmployers()
                .then((employer) => {
                    dispatch(loadEmployerSuccess(employer));
                })
                .catch((error) => {
                    throw error;
                });
        };
    }
    useEffect(() => {
        loadEmployers(dispatch);
        return () => {

        };
    }, [])

    return{employers};
}

export default useApp;
